import React, { useEffect, useState } from "react";
import { useTimeout } from "ahooks";
import PropTypes from "prop-types";
import { Spin, Image, Button } from "antd";
import QRCode from "qrcode";
// relative
import { autoPrefix } from "utils";
import styles from "./index.less";

const cx = autoPrefix(styles.prefix);

const Qrcode = ({ width = 200, height, value, timeout, refresh }) => {
  // state
  const [loading, setLoading] = useState(false);
  const [url, setUrl] = useState("");
  const [delay, setDelay] = useState(undefined);
  const [maskVisible, setMaskVisible] = useState(false);

  useTimeout(() => {
    setMaskVisible(true);
    setDelay(undefined);
  }, delay);

  useEffect(() => {
    async function generateUrl() {
      setLoading(true);
      try {
        const res = await QRCode.toDataURL(value);
        setUrl(res);
        setDelay(timeout);
        setMaskVisible(false);
      } catch (err) {
        setUrl("error");
      } finally {
        setLoading(false);
      }
    }
    generateUrl();
  }, [value, setLoading, setUrl, timeout, setDelay, setMaskVisible]);

  const onMaskHide = () => {
    typeof refresh === "function" && refresh();
  };

  return (
    <Spin
      wrapperClassName={cx("container")}
      style={{ width, height }}
      spinning={loading}
    >
      <div className={cx("content")}>
        <Image
          preview={false}
          width={width}
          height={height}
          src={url}
          fallback="data:image/png;base64,"
        />
        {maskVisible && (
          <div className={cx("mask")}>
            <Button type="link" onClick={onMaskHide}>
              Link
            </Button>
          </div>
        )}
      </div>
    </Spin>
  );
};

QRCode.propTypes = {
  width: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  height: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  value: PropTypes.string.isRequired,
  timeout: PropTypes.number,
  refresh: PropTypes.func,
};

console.log("QRCode", QRCode, PropTypes);

export default React.memo(Qrcode);
